css:
table{
  border: 1px solid black;
  border-collapse: collapse;
}
.table2 tr td{
  border: 1px solid black;
  border-collapse: collapse;
}
.table2 th{
  border: 1px solid black;
  border-collapse: collapse;
}
.table3 tr{
  border: 1px solid black;
  border-collapse: collapse;
}

html:
{% set ig = frappe.get_doc('Address',doc.company_address)%}
 {% set cs = frappe.get_doc('Address',doc.customer_address)%}


 {% set acc = frappe.db.get_value('Bank Account',{"is_default":1,"is_company_account":1},['name'])%}
 {%if acc%}
 {% set bank = frappe.get_doc('Bank Account',acc.name)%}
    {% endif %}
  <div class="table1">
          <table  style="width:100%;border:1px solid black;">
              <tr>
                  <td>Invoice</td>
                  <td>{{doc.name}}</td>
                  <td> Invoice Date</td>
                  <td>{{doc.posting_date}}</td>
                  <td>Time</td>
                  <td>{{doc.posting_time}}</td>
              </tr>
              <tr>
                  <td>Name</td>
                  <td>{{doc.customer}}</td>
                  <td>Vehicle no.</td>
                  <td>{{doc.vehicle_no}}</td>
            </tr>
              <tr>
                  <td>Address</td>
                  <td>{{doc.territory}}</td>
                  <td>Party GST No.</td>
                  <td>{{doc.customer_gstin}}</td>
            </tr>
          </table>
        </div>

<div class="table2" style="width:100%;">
    <table style="width:100%;border:1px solid black;">
        <tr>
            <th>Sr No.</th>
            <th>item Name</th>
            <th>HSN/SAC</th>
            <th>Crate/Box</th>
            <th>Qty</th>
            <th>litre</th>
            <th>Rate</th>
            <th>Amount</th>
            <th>Less Disc</th>
            <th>Taxable Amount</th>
            {% if ig.gst_state_number == cs.gst_state_number %}
            <th>CGST Amount</th>
            <th>Sgst Amount</th>
            {% endif %}
            {% if ig.gst_state_number != cs.gst_state_number %}
            <th>IGST Amount</th>
            {% endif %}
            <th>Total Amount</th>
        </tr>
        {%for i in doc.items%}
        <tr >
            <td>{{loop.index}}</td>
            <td>{{i.item_name}}</td>
            <td>{{i.gst_hsn_code}}</td>
            <td>{{doc.total_crate_qty}}</td>
            <td>{{i.qty}}</td>
            <td>{{i.total_weight}}</td>
            <td>{{i.rate}}</td>
            <td>{{i.amount}}</td>
            <td></td>
            <td>{{i.amount}}</td>
            {%set item%}{%endset%}
            {% if i.item_tax_template %}
                {%set item = frappe.get_doc('Item Tax Template',i.item_tax_template)%}
              {% endif %}
             {% if ig.gst_state_number == cs.gst_state_number %}
            <td>{%if item%}{{(item.tax_rate * i.amount)/100}}{%else%}0.0{%endif%}</td>
            <td>{%if item%}{{(item.tax_rate * i.amount)/100}}{%else%}0.0{%endif%}</td>
            <td>{%if item%}{{((item.tax_rate * i.amount)/100) + i.amount}}{%else%}0.0{%endif%}</td>
             {% endif %}
            {% if ig.gst_state_number != cs.gst_state_number %}
             <td>{%if item%}{{(item.tax_rate * 2*i.amount)/100}}{%else%}0.0{%endif%}</td>
             <td>{%if item%}{{((item.tax_rate * i.amount)/100) + i.amount}}{%else%}0.0{%endif%}</td>
            {% endif %}

        </tr>
        {% endfor %}
    </table>
</div>

<div  class="table3" style="width:100%;" >
    <table style="width:100%;">
        <tr style="width:150%;">
            <td style="width:600px;">Amt In word Rs:</td>
            <td style="width:1200px;" >{{ frappe.utils.money_in_words(doc.grand_total) }}</td>
             <td></td>
            <td style=" border: 1px solid black;width:700px;">Total Amount Before Tax</td>
            <td style=" border: 1px solid black;" class ="text-right">{{doc.total}}</td>
        </tr>

        <tr>
            <td>DueBalance:</td>
            {%set bal= doc.party_balance%}
            <td>{{bal}}</td>
            <td></td>
            {% set c = 0.0 %}
             {%for i in doc.items%}
             {% if i.item_tax_template %}
             {%set item = frappe.get_doc('Item Tax Template',i.item_tax_template)%}
                {% set c = c+  (item.tax_rate * i.amount)/100 %}{% endif %}
              {% endfor %}
             {% if ig.gst_state_number == cs.gst_state_number %}
                <td style=" border: 1px solid black;width:20%;"> CGST Amount</td>
                <td style=" border: 1px solid black;">{{c}} </td>
            {% endif %}
        </tr>
        <tr>
            <td>GST NO :</td>
            <td>{{doc.company_gstin}}</td>
            <td style="width:20%;">FSSAI LIC.NO.:</td>

             {% set b = 0.0 %}
             {%for i in doc.items%}
             {% if i.item_tax_template %}
             {%set item = frappe.get_doc('Item Tax Template',i.item_tax_template)%}
                {% set b = b + (item.tax_rate * i.amount)/100 %}{% endif %}
              {% endfor %}
             {% if ig.gst_state_number == cs.gst_state_number %}
                <td style=" border: 1px solid black;"> SGST Amount</td>
                <td style=" border: 1px solid black;"> {{b}}</td>
             {% endif %}

        </tr>
        <tr>

        </tr>
    </table>

    <table style="width:100%;">
  <tr>
   <td rowspan ="{{ rowspan }}" style ="width:57%;">
      <p>Gst Payable RS Charges:</p>
      <p>Certified that the particutars given above are true and correct.</p>
      <b>Bank Details : </b>
      {% set acc = frappe.db.get_value('Bank Account',{"is_default":1,"is_company_account":1},['name'])%}
         {%if acc%}
         {% set bank = frappe.get_doc('Bank Account',acc.name)%}

      <p>Bank Name:  &nbsp; &nbsp; {{bank.account_name}}</p>
      <p>Bank CC.Account No.&nbsp; &nbsp; {{bank.bank_account_no}} &nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bank Branch IFSC:</p>{% endif %}

   </td>
    {% set a = 0.0 %}
    {%for i in doc.items%}
    {% if i.item_tax_template %}
    {%set item = frappe.get_doc('Item Tax Template',i.item_tax_template)%}
     {%set a = a + (item.tax_rate * 2*i.amount)/100 %} {% endif %}
     {% endfor %}
    {% if ig.gst_state_number != cs.gst_state_number %}
       <td style ="border: 1px solid black;">IGST Amount:</td>
       <td style ="border: 1px solid black;">{{a}}</td>
    {% endif %}


  </tr>

  <tr>
     <td></td>
     <td style ="border: 1px solid black;">Round off Amount</td>
     <td>{{doc.rounded_total}}</td>

  </tr>
<tr>
   <td></td>

     <td style ="border: 1px solid black;"><b>Total Amount After Tax</b></td>
     <td>{{doc.grand_total}}</td> 
</tr>






 </table>

</div>
