css:
.print-format{
    margin:0px;


}
.print-format table{
    width:80%;
    border:1px solid;



}


.print-format th{
    color:black;
    padding:0px !important;


}
.print-format td{
    color:black;
    padding:0px !important;


}
span{

    font-size:5px;
    height:5px !important;
    padding:0px !important;
    margin:0px !important;


}
 @page {
            size: A6 portrait;
            margin: 0;
        }

html:
{% set a %}{% endset %}
   {% set c=frappe.db.get_value("Bank Account",{"party_type":"Supplier","party":doc.supplier},["name"] )%}
   {% if c %}

   {% set a=frappe.get_doc("Bank Account",{"party_type":"Supplier","party":doc.supplier})%}
   {% endif %}


    <table style="width:80%;border:1px solid black;border-bottom:none;">
    <tr>
        <td>
            {% if letter_head %}
                {{ letter_head }}
            {% endif %}
        </td>
    </tr>
    </table>
     <table style="border-bottom:none;"><tr><th style="width:80%;text-align:center;">
        <b style="font-size:8px;"> Milk Purchase Bill</b>
     </th></tr></table> 
    <table style="width:80%;">
        <tr style="width:80%;">

           <td style="width:40%;">
            <b> <span style="font-size:8px;">Payment Period: {{get_purchase(doc.name)[0].posting_date}} to {{get_purchase(doc.name)[-1].posting_date}}</span></b>
          </td> 
          <td style="width:40%;">
          <b ><span style="font-size:8px;">Print Date: {{frappe.utils.formatdate(frappe.utils.nowdate(),"dd-mm-yyyy")}}</span></b>
        </td>
        </tr>

    </table>

    <table style="width:80%;border:1px solid;border-bottom:none;border-top:none;">
        <tr style="width:80%;">
           <td style="width:40%;font-size:8px;">
             {% set s %}{% endset %}
       {% if doc.supplier %}
       {% set s = frappe.get_doc("Supplier",doc.supplier) %}
       {% endif %}

          <span style="font-size:8px;">Code No:</span> 
          {% if doc.supplier %}
          <span style="font-size:8px;">{{s.name}}</span><br>
          {% endif %}
          <span style="font-size:8px;">Bank Name:</span> 
          {% if a.bank %}
          <span style="font-size:8px;">{{a.bank}}</span>
          {% endif %}
          </td> 
          <td style="width:40%;font-size:8px;">
          <span style="font-size:8px;">Name:</span>
          {% if doc.supplier %}
          <span style="font-size:8px;">{{s.supplier_name}}</span><br>
          {% endif %}
          <span style="font-size:8px;">Mobile No:</span> 
          <span style="font-size:8px;"></span>
        </td>
        </tr>

    </table>

    <table style="width:80%;border:1px solid black;border-bottom:none;">
        <tr>

            <th style="border-left:1px solid;text-align:center;font-size:8px;color:black;">Date</th>
            <th style="border-left:1px solid;text-align:center;font-size:8px;color:black;">Shift</th>
            <th style="border-left:1px solid;text-align:center;font-size:8px;color:black;">Ltr</th>
            <th style="border-left:1px solid;text-align:center;font-size:8px;color:black;">Fat</th>
            <th style="border-left:1px solid;text-align:center;font-size:8px;color:black;">SNF</th>
            <th style="border-left:1px solid;text-align:center;font-size:8px;color:black;">Rate</th>
            <th style="border-left:1px solid;text-align:center;font-size:8px;color:black;">Amount</th>
        </tr>
        {%-for itm in get_purchase(doc.name)-%}


        <tr style="border:1px solid black;">
            <td style="border-left:1px solid;text-align:center;font-size:8px;">{{itm.posting_date}}</td>
            <td style="border-left:1px solid;text-align:center;font-size:8px;">{{itm.shift}}</td>
            <td style="border-left:1px solid;text-align:center;font-size:8px;">{{itm.ltr}}</td>
            <td style="border-left:1px solid;text-align:center;font-size:8px;">{{itm.fat}}</td>
            <td style="border-left:1px solid;text-align:center;font-size:8px;">{{itm.snf}}</td>
            <td style="border-left:1px solid;text-align:center;font-size:8px;">{{itm.rate}}</td>
            <td style="border-left:1px solid;text-align:center;font-size:8px;">{{itm.amount}}</td>

        </tr>
        {%-endfor-%}

        <tr style="border:1px solid black;">
            <td style="border-left:1px solid;text-align:left;font-size:8px;"></td>
            <td style="border-left:1px solid;text-align:left;font-size:8px;">Total</td>
            <td style="border-left:1px solid;text-align:right;font-size:8px;">{{doc.total_qty}}</td>
            <td style="border-left:1px solid;text-align:right;font-size:8px;"></td>
            <td style="border-left:1px solid;text-align:right;font-size:8px;"></td>
            <td style="border-left:1px solid;text-align:right;font-size:8px;"></td>
            <td style="border-left:1px solid;text-align:right;font-size:8px;">{{doc.get_formatted("total")}}</td>
        </tr>

    </table>
    <table style="width:80%;border:1px solid;border-bottom:none;border-top:none;">
        <tr style="width:40%;">
           <td style="border:1px solid;border-bottom:none;border-top:none;">
            <b><span style="font-size:8px;text-align:center;">Pay Amount</span></b>
          </td> 
          <td style="width:40%;">
          <b><span style="font-size:8px;text-align:center;">Add Ded/Amount</span></b>
        </td>
        </tr>

    </table>
    <table style="width:80%;border:1px solid;">
        <tr style="width:80%;">
           <td style="border:1px solid;">
             {% set s %}{% endset %}
       {% if doc.supplier %}
       {% set s = frappe.get_doc("Supplier",doc.supplier) %}
       {% endif %}

          <span style="font-size:8px;">Commission Head:</span> 
          <span style="font-size:8px;"></span><br>
          <span style="font-size:8px;">Bill Difference:</span> 
          <span style="font-size:8px;"></span><br>
          <span style="font-size:8px;">Difference Rate:</span> 
          <span style="font-size:8px;"></span><br>
          <span style="font-size:8px;">Salary:</span> 
          <span style="font-size:8px;"></span><br>

          </td> 
          <td style="width:40%;">
              {% set vars = {'jcase': 0} %}
              {%- for i in doc.items -%}
              {% set r %}{% endset %}
       {% if i.purchase_receipt %}
       {% set r = frappe.get_doc("Purchase Receipt",i.purchase_receipt) %}

       {% endif %}




                     {%- for item in r.items -%}
                    {%set a=frappe.get_doc("Milk Entry",item.milk_entry)%}
                    {% if vars.update({'jcase': vars.jcase + a.incentive}) %}{% endif %}

        {%- endfor -%}
        {%- endfor -%}

          <span style="font-size:8px;">Bonus Amount: {{vars.jcase}}</span>

        </td>
        </tr>

    </table>
    <table style="width:80%;border:1px solid;border-top:none;">
        <tr style="width:80%;font-size:8px;">
           <td style="width:30%;">
            <b> <span style="font-size:8px;">Total Amount: {{doc.get_formatted('rounded_total')}}</span></b>
          </td> 
          <td style="width:25%;font-size:8px;">
                {% set var= {'icase': 0} %}
              {%- for i in doc.items -%}
              {% set r %}{% endset %}
               {% if i.purchase_receipt %}
               {% set r = frappe.get_doc("Purchase Receipt",i.purchase_receipt) %}

               {% endif %}


                     {%- for item in r.items -%}
                    {%set a=frappe.get_doc("Milk Entry",item.milk_entry)%}
                    {% if var.update({'icase': var.icase + a.fat_deduction+a.snf_deduction}) %}{% endif %}

        {%- endfor -%}
         {%- endfor -%}

          <b><span style="font-size:8px;">Deduction :{{'%0.2f'| format(var.icase|float)}}</span></b>
        </td>
        <td style="width:25%;font-size:8px;">
          <b><span style="font-size:8px;">Net Pay: {{doc.get_formatted('rounded_total')}}</span></b>
        </td>
        </tr>

    </table>
